import React, { useState } from 'react';
import {useDispatch} from 'react-redux';
import {signOut} from './reducks/users/operations';
import {TransitionButton} from './components/UIkit';
const API = () => {
    const dispatch = useDispatch();
    const [items, setItem] = useState(Object);
    const [times, setTime] = useState(Object);
        fetch("https://api.exchangeratesapi.io/latest")
          .then(res => res.json())
          .then(json => {
            setItem({
              items: json.rates
            });
            setTime({
                times: json.date
              });
          });

     const JPY= (Object.keys(items).map(key => (
        items[key].JPY)
      ));
      const USD= (Object.keys(items).map(key => (
        items[key].USD)
      ));

      const time= (Object.keys(times).map(key => (
        items[key])
      ));


const exchange = (JPY/USD).toFixed(3);
    return(
        <div>
              <div>
                <h2 className="center" >為替　米ドル→日本円</h2>
                  <p className="center" > {exchange}円</p>
                  <p>最新更新日：{time}</p>

            </div>
            <TransitionButton
        label={"ログアウト"}
        onClick={() => dispatch(signOut())} 
        />
        </div>


    )
}

export default API
