import React, {useState, useCallback} from 'react';
import {Input} from '../components/UIkit'
const SignUp = () => {

    const   [username, setUsername] = useState(""),
            [email, setEmail] = useState(""),
            [password, setPassword] = useState(""),
            [confirmPassword, setConfirmPassword] = useState("");

    const inputUsername = useCallback( (event) => {
        setUsername(event.target.value)
    },[setUsername]);

    const inputEmail = useCallback( (event) => {
        setEmail(event.target.value)
    },[setEmail]);

    const inputPassword = useCallback( (event) => {
        setPassword(event.target.value)
    },[setPassword]);

    const inputConfirmPassword = useCallback( (event) => {
        setConfirmPassword(event.target.value)
    },[setConfirmPassword]);

    return (
        <div>
            <h2>アカウント登録</h2>
            <div></div>
            <Input 
                fullWidth={true}
                label={"ユーザ名"}
                margin="dense"
                multiline={false}
                required={true}
                rows={1}
                value={username}
                type={"text"}
                onCange={inputUsername}
            />
            <Input 
                fullWidth={true}
                label={"メールアドレス"}
                margin="dense"
                multiline={false}
                required={true}
                rows={1}
                value={email}
                type={"email"}
                onCange={inputEmail}
            />

            <Input 
                fullWidth={true}
                label={"パスワード"}
                margin="dense"
                multiline={false}
                required={true}
                rows={1}
                value={password}
                type={"password"}
                onCange={inputPassword}
            />
            <Input 
                fullWidth={true}
                label={"パスワード(確認用)"}
                margin="dense"
                multiline={false}
                required={true}
                rows={1}
                value={confirmPassword}
                type={"password"}
                onCange={inputConfirmPassword}
            />
        </div>
    )
}
export default SignUp
